    public function getSourceContext()
    {
        return new Twig_Source("{% extends '::layout/layoutAdmin.html.twig' %}

{% block body %}
    <h1>Liste des commandes</h1>

    <table class=\"table table-striped table-hover\">
        <thead>
            <tr>
                <th>Date</th>
                <th>Référence</th>
                <th>Client</th>
                <th>Actions</th>
            </tr>
        </thead>
        <tbody>
        {% for commande in commandes %}
            <tr>
                <td>{{ commande.date|date('d-m-Y h:i:s') }}</td>
                <td>{{ commande.reference }}</td>
                <td>{{ commande.utilisateur.username }}</td>
                <td>
                    <a href=\"{{ path('adminShowFacture', { 'id' : commande.id }) }}\"><i class=\"icon-download-alt\"></i></a>
                </td>
            </tr>
        {% endfor %}
        </tbody>
    </table>
{% endblock %}", "EcommerceEcommerceBundle:Administration:commandes/layout/index.html.twig", "/home/maxime/NetBeansProjects/ecommerce/src/Ecommerce/EcommerceBundle/Resources/views/Administration/commandes/layout/index.html.twig");
